sys_variable_value.value:
(function execute(inputs, outputs) {

var request = new sn_ws.RESTMessageV2();
request.setEndpoint(inputs.Instance+'/api/1584389/cicd/'+inputs.remoteID);
request.setHttpMethod('GET');

//Eg. UserName="admin", Password="admin" for this code sample.
var user = 'admin';
var password = 'Password@123';

request.setBasicAuth(user,password);
request.setRequestHeader("Accept","application/json");

var response = request.execute();
var body=JSON.parse(response.getBody());
var resultArray=body.result;

resultArray.forEach(function(record) {
  var gr = new GlideRecord('x_601332_cicd_logs');
  gr.initialize();
    gr.setValue('disposition', record.disposition);
    gr.setValue('item',record.remote_update);
    gr.setValue('file_diferences', record.payload);
    gr.setValue('update_set', record.update_set_name);
    record.probelmSysID?gr.setValue('problem_id', record.probelmSysID):'';
    gr.description=record.description?record.description:'No issue found';
    gr.setValue('task',inputs.task_id);
  
  gr.insert();
});

outputs.response=body;

})(inputs, outputs);

