sys_variable_value.value:
(function execute(inputs, outputs) {

var request = new sn_ws.RESTMessageV2();
request.setEndpoint(inputs.instance+'/api/1584389/cicd/'+inputs.action+'/'+inputs.RemoteId+'?'+inputs.queryParam);
request.setHttpMethod('GET');


var user = 'admin';
var password = gs.getProperty('x_601332_cicd.Remote.Instance.Password');

request.setBasicAuth(user,password);
request.setRequestHeader("Accept","application/json");

var response = request.execute();
var body=JSON.parse(response.getBody());
var status=response.getStatusCode();

var grPack= new GlideRecord('x_601332_cicd_deployment_pack');
grPack.get(inputs.packSysId.toString());
grPack.notes=grPack.added_instance.name+': '+body.result.message;
if(status==200){
 grPack.state='Loaded';
}
grPack.update();

outputs.response=body;
outputs.status=response.getStatusCode();

})(inputs, outputs);

